import React, { Component } from 'react';
import './assets/tree.css'

export default () => {
    const data = [
        {
            id: 0, title: '河北', children: [
                { id: 10, title: '石家庄' },
                { id: 11, title: '唐山', open: 'closed', checked: '' },
                { id: 12, title: '秦皇岛', open: 'closed', checked: '' },
                { id: 13, title: '邯郸', open: 'closed', checked: '' },
            ]
        },
        { id: 1, title: '北京', open: 'closed', checked: '' },
        { id: 2, title: '上海', open: 'closed', checked: 'checked' }
    ]
    function dataInit(data) {
        data.forEach(item => {
            if (item.children && item.children.length > 0) { // item has children
                item.open='open';
                dataInit(item.children)
            } else{
                item.open = '';
            }

        })
    }
    dataInit(data);


    const Tree = ({ data }) => (
        <ul className='tree'>
            {data.map(item => (
                <li className={`tree-item ${item.open}`} key={item.id}>
                    <div className='tree-title'>
                        <div className={`folder ${item.open}`} />
                        <div className={`checkbox ${item.checked}`} />
                        <div className='item'>{item.title}</div>
                    </div>
                    {item.children ? (
                        <Tree data={item.children} />
                    ) : ''}
                </li>
            ))}
        </ul>

    )
    return <Tree data={data} />
}
