import React, { useState } from 'react';
import './assets/tree.css'

export default () => {
    const [orgData, setData] = useState(dataInit([
        {
            id: 0, title: '浙江', children: [
                { id: 10, title: '杭州' },
                { id: 11, title: '宁波' },
                { id: 12, title: '绍兴' },
                { id: 13, title: '金华' },
            ]
        },
        { id: 1, title: '' },
        { id: 2, title: '上海' }
    ], null));


    function dataInit(data, parent) {
        return data.map(item => {
            if (item.children && item.children.length > 0) { // item has children
                item.open = 'close';
                item.children = dataInit(item.children, item);
            } else {
                item.open = '';
            }
            item.parent = parent;
            item.checked = '';
            return item
        });
    }

    function getChecked(item) {
        var childUnchecked = item.children.map(item => item.checked).indexOf('') != -1;
        var childChecked = item.children.map(item => item.checked).indexOf('checked') != -1;
        var childHalf = item.children.map(item => item.checked).indexOf('half') != -1;
        if (childUnchecked && !childChecked && !childHalf) return ''  //子选项都为空 父选项为空
        else if (childChecked && !childUnchecked && !childHalf) return 'checked' //子选项全选 父选项为全选
        else return 'half' //子选项部分选择 父选项为半选
    }
    function adjustChecked(item) {
        let parent = item.parent;
        while (parent) {  //循环父级的父级
            parent.checked = getChecked(parent);
            parent = parent.parent;
        }
        if(item.children && item.children.length > 0) adjust(item.children, item.checked);
        function adjust(arr, checked){
            arr.forEach(item =>{
                item.checked = checked;
                if (item.children && item.children.length > 0) adjust(item.children, item.checked);
            })
        }

    }


    const Tree = ({ data }) => (
        <ul className='tree'>
            {data.map(item => (
                <li className={`tree-item ${item.open}`} key={item.id}>
                    <div className='tree-title'>
                        <div className={`folder ${item.open}`} onClick={() => {
                            if (item.open !== '') {
                                if (item.open == 'open') {
                                    item.open = 'close';
                                } else {
                                    item.open = 'open';
                                }
                                setData([...orgData]);
                            }
                        }} />
                        <div className={`checkbox ${item.checked}`} onClick={() => {
                            if (item.checked == '') {
                                item.checked = 'checked';
                            } else {
                                item.checked = '';
                            }
                            adjustChecked(item);

                            setData([...orgData]);
                        }} />
                        <div className='item'>{item.title}</div>
                    </div>
                    {item.children ? (
                        <Tree data={item.children} />
                    ) : ''}
                </li>
            ))}
        </ul>
    );
    return <Tree data={orgData} />
}
